# --- Funciones de soporte para la compilaci贸n (Mantenidas) ---
def move_file_and_cleanup(carpeta_temp, temp_file_path, extension, output_folder, nombre_base, writer):
    """Mueve el archivo final y elimina la carpeta temporal."""
    try:
        if extension == ".app":
            source_dir = os.path.join(carpeta_temp, "dist", f"{nombre_base}.app")
            dest_dir = os.path.join(output_folder, f"{nombre_base}.app")
            if os.path.exists(source_dir):
                if os.path.exists(dest_dir):
                    shutil.rmtree(dest_dir) 
                shutil.move(source_dir, dest_dir)
                writer.write(f"\n[OK] Aplicaci贸n final movida a: {dest_dir}\n")
                return 0
            else:
                writer.write("\n[ERROR] No se encontr贸 la carpeta .app final.\n")
                return 1
        elif extension == ".exe":
            source_file = os.path.join(carpeta_temp, "dist", f"{nombre_base}.exe")
            dest_file = os.path.join(output_folder, f"{nombre_base}.exe")
            if os.path.exists(source_file):
                shutil.move(source_file, dest_file)
                writer.write(f"\n[OK] Ejecutable final movido a: {dest_file}\n")
                return 0
            else:
                writer.write("\n[ERROR] No se encontr贸 el archivo .exe final.\n")
                return 1
        return 1
    except Exception as e:
        writer.write(f"\n[ERROR al mover/limpiar]: {e}\n")
        return 1
    finally:
        try:
            if os.path.exists(carpeta_temp):
                shutil.rmtree(carpeta_temp)
        except Exception as e:
            writer.write(f"\n[ADVERTENCIA] No se pudo limpiar la carpeta temporal: {e}\n")
