#  FUNCIN DE RUTA DE RECURSOS (Manejador de PyInstaller)
def resource_path(relative_path):
    """
    Obtiene la ruta absoluta al recurso, funciona tanto para
    el modo de desarrollo como para el ejecutable de PyInstaller.
    """
    try:
        base_path = sys._MEIPASS
    except Exception:
        base_path = os.path.abspath(os.path.dirname(__file__))

    return os.path.join(base_path, relative_path)
