# ---------- Utilidades de configuraci贸n persistente ----------
def _cargar_config():
    """ Carga la configuraci贸n. """
    if not os.path.exists(CONFIG_FILE):
        if os.path.exists(INITIAL_CONFIG_FILE):
            try:
                shutil.copy2(INITIAL_CONFIG_FILE, CONFIG_FILE)
            except Exception as e:
                messagebox.showerror("Error Cr铆tico", f"No se pudo copiar el config.json inicial: {e}")
                sys.exit()
        else:
            try:
                with open(CONFIG_FILE, "w", encoding="utf-8") as f:
                    json.dump({}, f, ensure_ascii=False, indent=2)
            except Exception as e:
                    messagebox.showerror("Error Cr铆tico", f"No se pudo crear config.json: {e}")
                    sys.exit()
    
    try:
        with open(CONFIG_FILE, "r", encoding="utf-8") as f:
            return json.load(f)
    except Exception as e:
        messagebox.showerror("Error al Cargar Config", f"No se pudo leer {CONFIG_FILE}: {e}")
        return {}
    
def _guardar_config(cfg):
    """ Guarda la configuraci贸n. """
    try:
        with open(CONFIG_FILE, "w", encoding="utf-8") as f:
            json.dump(cfg, f, ensure_ascii=False, indent=2)
    except Exception as e:
        print(f"Advertencia: No se pudo guardar config.json en {DATA_PATH}: {e}")
        pass

