# ------------------ PROPER REFINADO / SANEAMIENTO ------------------
_SMALL_WORDS = {
    "de","del","la","el","los","las","y","o","u","en","con","por","para",
    "a","al","un","una","entre","sin","sobre","desde","hasta","contra","segun","seg煤n","al"
}

def proper_refinado(texto):
    if texto is None:
        return ""
    s = str(texto).strip()
    if not s:
        return ""
    s = re.sub(r"\s+", " ", s)
    words = s.split(" ")
    out = []
    for i, w in enumerate(words):
        if not w:
            continue
        w_clean = w.strip()
        lw = w_clean.lower()
        if lw.isdigit():
            out.append(w_clean)
            continue
        
        if lw in _SMALL_WORDS and i not in (0, len(words)-1):
            out.append(lw)
        else:
            out.append(lw.capitalize())
    return " ".join(out)

#  CORRECCIN 1: Saneamiento m谩s agresivo para Cloudinary (solo ASCII)
def safe_filename(s):
    if s is None:
        return ""
    st = str(s).strip()
    # Eliminar acentos y caracteres no ASCII
    st = unicodedata.normalize('NFKD', st).encode('ascii', 'ignore').decode('ascii')
    # Eliminar caracteres no deseados
    st = re.sub(r'[\\/:*?"<>|]+', "", st)
    # Reemplazar espacios por guiones para URL safety, pero solo en el nombre base
    st = re.sub(r"\s+", " ", st).strip()
    return st or "SinNombre"

