# ---------------- FUNCIONES GENERALES ----------------
def limpiar_texto(texto):
    if not isinstance(texto, str):
        return texto
    texto = unicodedata.normalize("NFKD", texto)
    texto = texto.encode("ascii", "ignore").decode("ascii")
    texto = re.sub(r"[^a-zA-Z0-9.,:;!?()\"' \-]+", " ", texto)
    texto = re.sub(r"\s+", " ", texto).strip()
    return proper_refinado(texto)

# --- Helpers m铆nimos de fecha ---
import datetime as _dt
import re as _re

_MESES = ["enero","febrero","marzo","abril","mayo","junio","julio","agosto",
      "septiembre","octubre","noviembre","diciembre"]
_DIAS = ["lunes","martes","miercoles","jueves","viernes","sabado","domingo"]

def _parse_fecha_any(fecha):
    if fecha is None: return None
    if isinstance(fecha, _dt.datetime): return _dt.datetime(fecha.year, fecha.month, fecha.day)
    if isinstance(fecha, _dt.date): return _dt.datetime(fecha.year, fecha.month, fecha.day)
    s = str(fecha).strip()
    if not s: return None
    try:
        if _re.fullmatch(r"\d+(\.0+)?", s):
            dias = int(float(s))
            base = _dt.datetime(1899, 12, 30)
            return base + _dt.timedelta(days=dias)
    except: pass

    for fmt in ("%Y-%m-%d", "%d/%m/%Y", "%d-%m-%Y", "%m/%d/%Y",
          "%Y/%m/%d", "%d/%m/%y", "%d-%m/%y"):
        try: return _dt.datetime.strptime(s, fmt)
        except: pass
    try: return _dt.datetime.fromisoformat(s.replace("Z",""))
    except: return None

def formatear_fecha(fecha_str, uso, solo_nombre=False):
    dt = _parse_fecha_any(fecha_str)
    if not dt: return str(fecha_str or "")
    uso_norm = (uso or "").strip().lower()
    
    if solo_nombre:
        mes = _MESES[dt.month-1].capitalize()
        return f"{mes} {dt.day:02d} de {dt.year}"
    if uso_norm == "postales":
        dia = _DIAS[dt.weekday()]
        mes = _MESES[dt.month-1]
        return f"{dia}, {dt.day:02d} de {mes} de {dt.year}"
    mes = _MESES[dt.month-1].capitalize()
    return f"{mes} {dt.day:02d} de {dt.year}"

