def generar_nombre_archivo(fecha_str, tipo, sucursal, ruta, punto, municipio, extension="JPG"):
    fecha_fmt = formatear_fecha(fecha_str, uso="Postales", solo_nombre=True)
    ts = " ".join(x.strip() for x in [str(tipo or ""), str(sucursal or "")] if x and x.strip())
    partes_ubic = [str(x).strip() for x in [ruta, punto, municipio] if x and str(x).strip()]
    ubic = ", ".join(partes_ubic)
    partes_final = [p for p in [fecha_fmt, ts, ubic] if p]
    nombre = " - ".join(partes_final)
    nombre = proper_refinado(nombre)
    nombre = _re.sub(r'[\\/:*?"<>|]+', "", nombre).strip()
    ext = str(extension or "JPG").lstrip(".")
    return f"{nombre}.{ext}"

# ----------------- Progreso (barra + estado) -----------------
_progress_ctx = {"bar": None, "label": None, "max": 100, "val": 0, "root": None}

def set_progress_widgets(bar, label, root):
    _progress_ctx["bar"] = bar
    _progress_ctx["label"] = label
    _progress_ctx["root"] = root

def progress_reset(max_steps):
    _progress_ctx["max"] = max_steps
    _progress_ctx["val"] = 0
    if _progress_ctx["bar"]:
        _progress_ctx["bar"]["maximum"] = max_steps
        _progress_ctx["bar"]["value"] = 0
    if _progress_ctx["label"]:
        _progress_ctx["label"].configure(text="Iniciando proceso...")
    if _progress_ctx["root"]:
        _progress_ctx["root"].update()
        time.sleep(0.01)

def progress_step(msg=None, inc=0):
    if _progress_ctx["label"] and msg is not None:
        _progress_ctx["label"].configure(text=msg)
    if inc > 0:
        _progress_ctx["val"] += inc
    
    if _progress_ctx["bar"]:
        _progress_ctx["bar"]["value"] = min(_progress_ctx["val"], _progress_ctx["max"])
    
    if _progress_ctx["root"]:
        _progress_ctx["root"].update()
        time.sleep(0.01)

# ---------------- FUNCIONES DE PPTX/IMAGENES/CONVERSION ----------------

def descargar_imagen(url, nombre):
    try:
        r = requests.get(url, stream=True, timeout=15)
        r.raise_for_status() 

        with open(nombre, "wb") as f:
            for chunk in r.iter_content(chunk_size=8192):
                if chunk: 
                    f.write(chunk)
        return nombre
    except requests.exceptions.RequestException as e:
        print(f"ERROR DE DESCARGA DE IMAGEN ({url}): {e}")
        return None

def insertar_imagen_en_placeholder(slide, shape, ruta_imagen):
    left, top, width, height = shape.left, shape.top, shape.width, shape.height
    try:
        sp = shape.element
        sp.getparent().remove(sp) 
    except Exception as e:
        print(f"Advertencia: No se pudo eliminar el placeholder de texto. Error: {e}")
        
    try:
        slide.shapes.add_picture(ruta_imagen, left, top, width=width, height=height)
    except Exception as e:
        raise Exception(f"Fallo al insertar la imagen en el PPTX: {e}") 

