# --- NUEVA FUNCIN: DETECCIN Y RECORTE DE BORDES NEGROS ---
def detectar_y_recortar_bordes_negros(image, umbral_negro=5, porcentaje_negro=0.98, franja_borde=10):
    """
    Detecta y recorta franjas de bordes negros (muy oscuros) de una imagen.
    """
    img_rgb = image.convert("RGB") # Aseguramos formato RGB
    ancho, alto = img_rgb.size

    def es_borde_negro_horizontal(y_start, y_end):
        """Verifica si una franja horizontal es negra."""
        pixels = img_rgb.crop((0, y_start, ancho, y_end)).getdata()
        negros = 0
        for r, g, b in pixels:
            if r <= umbral_negro and g <= umbral_negro and b <= umbral_negro:
                negros += 1
        return (negros / len(pixels)) >= porcentaje_negro

    def es_borde_negro_vertical(x_start, x_end):
        """Verifica si una franja vertical es negra."""
        pixels = img_rgb.crop((x_start, 0, x_end, alto)).getdata()
        negros = 0
        for r, g, b in pixels:
            if r <= umbral_negro and g <= umbral_negro and b <= umbral_negro:
                negros += 1
        return (negros / len(pixels)) >= porcentaje_negro

    # Recorte superior
    top_cut = 0
    for y in range(0, alto - franja_borde, franja_borde):
        if es_borde_negro_horizontal(y, y + franja_borde):
            top_cut = y + franja_borde
        else:
            break
            
    # Recorte inferior
    bottom_cut = alto
    for y in range(alto - franja_borde, top_cut, -franja_borde):
        if es_borde_negro_horizontal(y, y + franja_borde):
            bottom_cut = y
        else:
            break

    # Recorte izquierdo
    left_cut = 0
    for x in range(0, ancho - franja_borde, franja_borde):
        if es_borde_negro_vertical(x, x + franja_borde):
            left_cut = x + franja_borde
        else:
            break
            
    # Recorte derecho
    right_cut = ancho
    for x in range(ancho - franja_borde, left_cut, -franja_borde):
        if es_borde_negro_vertical(x, x + franja_borde):
            right_cut = x
        else:
            break
            
    if (left_cut >= right_cut or top_cut >= bottom_cut): # Recorte inv谩lido o imagen completamente negra
        return image # Devolver original si el recorte es muy agresivo

    if left_cut > 0 or right_cut < ancho or top_cut > 0 or bottom_cut < alto:
        print(f"Bordes negros recortados: L:{left_cut}, T:{top_cut}, R:{right_cut}, B:{bottom_cut}")
        return image.crop((left_cut, top_cut, right_cut, bottom_cut))
    
    return image # No se encontraron bordes negros significativos

# --- NUEVA FUNCIN: ESTIRAR HORIZONTALMENTE (RELLENO FORZADO V) ---
def ajustar_imagen_horizontal_estirar_v(img_recortada, ruta_imagen_ajustada, relacion_deseada=(4, 3)):
    """
    Toma una imagen (proporci贸n >= 4:3), la estira VERTICALMENTE para rellenar
    todo el lienzo 4:3, manteniendo el ancho original. (Modo: Estirar).
    """
    ancho_recortado, alto_recortado = img_recortada.size
    relacion_objetivo = relacion_deseada[0] / relacion_deseada[1]
    
    # 1. Definir las dimensiones del lienzo de salida 4:3 (basado en el ancho recortado)
    ancho_lienzo = ancho_recortado
    alto_lienzo = int(ancho_lienzo / relacion_objetivo) # Calcula la altura 4:3
    
    # 2. Reescalar la imagen para que rellene las dimensiones del lienzo (estiramiento forzado).
    img_final = img_recortada.resize(
        (ancho_lienzo, alto_lienzo),
        Image.LANCZOS
    )
    
    # 3. Guardar el resultado estirado. No se necesita m谩s recorte.
    img_final.save(ruta_imagen_ajustada, format='JPEG', quality=95)
    return True

# --- NUEVA FUNCIN: RELLENAR VERTICAL/CUADRADA CON ZOOM BLUR (RELLENO INTELIGENTE) ---
def ajustar_imagen_vertical_blur_h(img_recortada, ruta_imagen_ajustada, relacion_deseada=(4, 3)):
    """
    Toma una imagen (proporci贸n < 4:3) y la rellena con un fondo difuminado y aumentado.
    """
    ancho_recortado, alto_recortado = img_recortada.size
    relacion_objetivo = relacion_deseada[0] / relacion_deseada[1]
    
    # El lienzo debe tener la altura de la imagen recortada, pero el ancho de 4:3
    alto_lienzo = alto_recortado
    ancho_lienzo = int(alto_lienzo * relacion_objetivo)
    
    lienzo_final = Image.new("RGB", (ancho_lienzo, alto_lienzo), color=(0, 0, 0)) 

    # 1. Generar el fondo Blur
    scale_factor_blur_w = ancho_lienzo / ancho_recortado
    scale_factor_blur_h = alto_lienzo / alto_recortado
    scale_factor_blur = max(scale_factor_blur_w, scale_factor_blur_h) # Factor para cubrir el fondo
    
    img_para_fondo = img_recortada.resize(
        (int(ancho_recortado * scale_factor_blur), int(alto_recortado * scale_factor_blur)), 
        Image.LANCZOS
    )
    
    # Recortar el centro del fondo para que tenga las dimensiones exactas del lienzo
    x_crop_blur = (img_para_fondo.width - ancho_lienzo) // 2
    y_crop_blur = (img_para_fondo.height - alto_lienzo) // 2
    img_fondo_recortado = img_para_fondo.crop((x_crop_blur, y_crop_blur, x_crop_blur + ancho_lienzo, y_crop_blur + alto_lienzo))
    
    img_fondo_final = img_fondo_recortado.filter(ImageFilter.GaussianBlur(radius=70))
    lienzo_final.paste(img_fondo_final, (0, 0))
    
    # 2. Pegar la imagen principal sin reescalar, centrada
    posicion_x_original = (ancho_lienzo - ancho_recortado) // 2
    posicion_y_original = (alto_lienzo - alto_recortado) // 2
    lienzo_final.paste(img_recortada, (posicion_x_original, posicion_y_original))
    
    lienzo_final.save(ruta_imagen_ajustada, format='JPEG', quality=95)
    return True

# ---  FUNCIN AJUSTADORA CENTRAL (DISPATCHER) ---
def ajustar_imagen_a_horizontal(ruta_imagen_original, ruta_imagen_ajustada, relacion_deseada=(4, 3)):
    """
    Funci贸n que decide si usar Estiramiento Vertical (para horizontales) o
    Relleno con Blur (para verticales/cuadradas) despu茅s de recortar bordes negros.
    """
    try:
        img_original = Image.open(ruta_imagen_original).convert("RGB")
    except Exception as e:
        print(f"Error al abrir/convertir la imagen: {e}")
        return False

    # 1. Detectar y recortar bordes negros
    img_recortada = detectar_y_recortar_bordes_negros(img_original)
    
    ancho_recortado, alto_recortado = img_recortada.size
    relacion_objetivo = relacion_deseada[0] / relacion_deseada[1] # 4/3 = 1.333
    relacion_actual = ancho_recortado / alto_recortado

    if ancho_recortado <= 0 or alto_recortado <= 0:
        print("Advertencia: Imagen recortada con dimensiones cero.")
        return False

    # 2. DECISIN: 驴Horizontal (> 4:3) o Estrecha (< 4:3)?
    # Las im谩genes con relaci贸n >= 4:3 se estiran (incluyendo 16:9).
    # Las im谩genes con relaci贸n < 4:3 se les aplica Zoom Blur (incluyendo 1:1).
    if relacion_actual >= relacion_objetivo - 0.01:
        # Modo Horizontal: Estirar verticalmente (aplastar) para rellenar el 4:3.
        print("DEBUG: Despachando a Modo Estirar (Horizontal >= 4:3)")
        return ajustar_imagen_horizontal_estirar_v(img_recortada, ruta_imagen_ajustada, relacion_deseada)
    else:
        # Modo Vertical/Cuadrada: Rellenar con Zoom Blur (mantiene proporci贸n).
        print("DEBUG: Despachando a Modo Zoom Blur (Estrecha < 4:3)")
        return ajustar_imagen_vertical_blur_h(img_recortada, ruta_imagen_ajustada, relacion_deseada)


def ajustar_texto_placeholder(shape, texto, uso="Reportes"):
    if not shape.has_text_frame: return
    tf = shape.text_frame
    tf.clear()
    p = tf.paragraphs[0]
    p.alignment = PP_ALIGN.CENTER
    run = p.add_run()
    run.text = str(texto)
    run.font.bold = True
    run.font.color.rgb = COLOR_HEX
    max_size = Pt(32 if uso == "Postales" else 12)
    min_size = Pt(28 if uso == "Postales" else 10)
    box_w = shape.width
    box_h = shape.height
    current_size = max_size
    run.font.size = current_size
    while current_size.pt > min_size.pt:
        text_len = len(run.text)
        est_width = current_size.pt * 0.55 * text_len
        est_height = current_size.pt * 1.2
        if est_width <= box_w.pt and est_height <= box_h.pt:
            break
        current_size = Pt(current_size.pt - 1)
        run.font.size = current_size

def estilizar_shape_texto_existente(shape, uso="Reportes"):
    if not shape.has_text_frame: return
    tf = shape.text_frame
    for p in tf.paragraphs:
        p.alignment = PP_ALIGN.CENTER
        for r in p.runs:
            r.font.bold = True
            r.font.color.rgb = COLOR_HEX
            r.font.size = Pt(30 if uso=="Postales" else 11)

# ---  FUNCIN MEJORADA PARA COLOCAR IMAGEN ---
def colocar_imagen_placeholder(slide, shape, record_fields, record_id, campo, uso, puntos_img_total):
    fields = record_fields
    attachments = fields.get(campo,[])
    if not attachments:
        ajustar_texto_placeholder(shape, "(sin imagen)")
        return
    url = attachments[0].get("url")
    if not url:
        ajustar_texto_placeholder(shape, "(sin URL)")
        return
    
    #  RUTAS TEMPORALES PARA EL AJUSTE
    temp_path_original = os.path.join(TEMP_IMG, f"{record_id}_{campo}_orig.jpg")
    temp_path_ajustada = os.path.join(TEMP_IMG, f"{record_id}_{campo}_ajustada.jpg") 
    
    nombre_campo = campo.replace("_", " ").capitalize()
    
    inc_antes = 2 if uso == "Reportes" else 0
    progress_step(msg=f"Descargando: {nombre_campo}...", inc=inc_antes)
    
    ruta_guardada = descargar_imagen(url, temp_path_original)
    
    inc_despues = (puntos_img_total - inc_antes) if uso == "Reportes" else 0
    
    #  MODIFICACIN CLAVE: Definir los campos que SI se estiran (Modo Estiramiento Simple)
    campos_a_estirar_simple = ["Reporte firmado", "Lista de asistencia"]
    
    # Si el campo est谩 en la lista de estiramiento simple, NO usamos el ajuste avanzado/inteligente.
    usar_ajuste_inteligente = campo not in campos_a_estirar_simple
    
    if ruta_guardada and sys.modules.get('PIL'):
        
        if usar_ajuste_inteligente:
            # --- Modo: Recorte de Bordes Negros + Relleno Inteligente (Horizontal/Vertical) ---
            progress_step(msg=f"Ajustando proporci贸n/bordes negros de {nombre_campo}...", inc=inc_despues // 2)
            
            # ajustar_imagen_a_horizontal es el dispatcher que elige entre Estirar Verticalmente 
            # (para horizontales) o Zoom Blur (para verticales/cuadradas).
            ajuste_exitoso = ajustar_imagen_a_horizontal(
                ruta_guardada, 
                temp_path_ajustada, 
                relacion_deseada=(4, 3) 
            )

            progress_step(inc=inc_despues - (inc_despues // 2))

            if ajuste_exitoso:
                try:
                    # Usamos la imagen AJUSTADA (con la proporci贸n o blur correcta)
                    insertar_imagen_en_placeholder(slide, shape, temp_path_ajustada) 
                except Exception as e:
                    ajustar_texto_placeholder(shape, f"(ERROR al insertar: {e})", uso="Reportes")
            else:
                ajustar_texto_placeholder(shape, f"(Fallo al ajustar orientaci贸n de {nombre_campo})", uso="Reportes")
                
            # Limpieza
            try: 
                os.remove(ruta_guardada) 
                if os.path.exists(temp_path_ajustada):
                    os.remove(temp_path_ajustada)
            except: pass
            
        else:
            # --- Modo: Estiramiento Simple (para Reporte/Lista) ---
            progress_step(msg=f"Estirando {nombre_campo} para rellenar completamente...", inc=inc_despues)
            try:
                # Estiramiento forzado para rellenar el placeholder.
                insertar_imagen_en_placeholder(slide, shape, ruta_guardada) 
            except Exception as e:
                ajustar_texto_placeholder(shape, f"(ERROR al insertar (estirar): {e})", uso="Reportes")
            
            # Limpieza
            try: 
                os.remove(ruta_guardada) 
            except: pass
            
    else:
        progress_step(inc=inc_despues)
        ajustar_texto_placeholder(shape, f"(Descarga de {nombre_campo} fall贸)", uso="Reportes")


def rellenar_pptx(record, plantilla_path, record_id=None, uso="Reportes", puntos_por_imagen=0):
    prs = Presentation(plantilla_path)
    campos = {k: limpiar_texto(v) if isinstance(v,str) else v for k,v in record.items()}

    fecha_iso = record.get("Fecha","")
    hora_raw = record.get("Hora","")
    fecha_larga = formatear_fecha(fecha_iso, uso)
    hora_fmt = formatear_hora(hora_raw, uso)
    confechor = fecha_larga + ("\n"+hora_fmt if hora_fmt else "")
    fecha_sola = formatear_fecha(fecha_iso, uso, solo_nombre=False)
    hora_sola = hora_fmt

    ruta = campos.get("Ruta a seguir","")
    punto = campos.get("Punto de reunion","")
    municipio = campos.get("Municipio","")
    sucursal = campos.get("Sucursal","")
    tipo_val = campos.get("Tipo","")

    partes = []
    if ruta: partes.append(ruta)
    if punto: partes.append(punto)
    if municipio: partes.append(municipio)
    concat_text = ", ".join(partes)

    consuc_text = f"Sucursal {sucursal}"

    for slide in prs.slides:
        for shape in list(slide.shapes):
            if not getattr(shape, "has_text_frame", False): continue
            text = (shape.text or "").strip()

            if text.startswith("<<") and text.endswith(">>"):
                campo = text[2:-2]
                if campo == "Confechor":
                    ajustar_texto_placeholder(shape, confechor, uso)
                elif campo == "Confecha":
                    ajustar_texto_placeholder(shape, fecha_sola, uso)
                elif campo == "Conhora":
                    ajustar_texto_placeholder(shape, hora_sola, uso)
                elif campo == "Concat":
                    ajustar_texto_placeholder(shape, concat_text, uso)
                elif campo in ("CONSUC", "Consuc"):
                    ajustar_texto_placeholder(shape, consuc_text, uso)
                else:
                    valor = campos.get(campo,"")
                    if isinstance(valor,list) and valor and isinstance(valor[0],dict) and "id" in valor[0]:
                        if record_id:
                            colocar_imagen_placeholder(slide, shape, campos, record_id, campo, uso, puntos_por_imagen)
                        else:
                            ajustar_texto_placeholder(shape, "(sin imagen)", uso)
                    else:
                        ajustar_texto_placeholder(shape, valor, uso)
            else:
                estilizar_shape_texto_existente(shape, uso)
    return prs

def _convertir_soffice(pptx_path, output_format, target_path):
    input_dir = os.path.dirname(pptx_path)
    base_name = os.path.splitext(os.path.basename(pptx_path))[0]
    
    if output_format.lower() == "pdf":
        conversion_format = "pdf"
    elif output_format.lower() == "jpg":
        conversion_format = "png" 
    else:
        return False

    try:
        command = [
            LIBREOFFICE_PATH,
            "--headless",
            f"--convert-to", conversion_format,
            "--outdir", input_dir,
            os.path.abspath(pptx_path)
        ]
        
        result = subprocess.run(command, check=True, capture_output=True, text=True, timeout=300)
        
        generated_ext = 'png' if output_format.lower() == 'jpg' else output_format.lower()
        generated_name = base_name + "." + generated_ext
        generated_path = os.path.join(input_dir, generated_name)
        
        found_path = None
        
        if os.path.exists(generated_path):
            found_path = generated_path
        
        if not found_path and generated_ext in ("png", "jpg", "jpeg"):
            indexed_output = os.path.join(input_dir, base_name + "-0." + generated_ext)
            if os.path.exists(indexed_output):
                found_path = indexed_output

        if found_path:
            if os.path.abspath(found_path) != os.path.abspath(target_path):
                if output_format.lower() == "jpg" and sys.modules.get('PIL'):
                    try:
                        img = Image.open(found_path)
                        if img.mode != 'RGB':
                            img = img.convert('RGB')
                        img.save(target_path, "JPEG", quality=95)
                        os.remove(found_path)
                    except Exception as img_e:
                        print(f"Advertencia: No se pudo convertir PNG a JPG ({img_e}). Dejando PNG: {found_path}")
                        os.rename(found_path, target_path)
                else:
                    os.rename(found_path, target_path)
            
            os.remove(pptx_path)
            return True
        else:
            messagebox.showerror("Error Conversi贸n", f"No se encontr贸 el archivo de salida final esperado. Log: {result.stderr}")
            return False

    except subprocess.CalledProcessError as e:
        messagebox.showerror(
            f"Error de Conversi贸n ({sys.platform})",
            f"La conversi贸n fall贸 para '{os.path.basename(pptx_path)}'. Aseg煤rate de que LibreOffice no est茅 ejecut谩ndose.\n\n"
            f"Detalles del Error (stderr):\n{e.stderr}"
        )
        return False
    except FileNotFoundError:
        messagebox.showerror("Error", f"El ejecutable de LibreOffice no fue encontrado en: {LIBREOFFICE_PATH}")
        return False
    except subprocess.TimeoutExpired:
        messagebox.showerror("Error de Tiempo", f"La conversi贸n para '{os.path.basename(pptx_path)}' tard贸 demasiado y fue cancelada.")
        return False
    except Exception as e:
        messagebox.showerror("Error Inesperado", f"Error al convertir {os.path.basename(pptx_path)}: {e}")
        return False

def pptx_a_pdf(pptx_path, pdf_path):
    if comtypes_available:
        if not _convertir_soffice(pptx_path, "pdf", pdf_path): pass
    else:
        if not _convertir_soffice(pptx_path, "pdf", pdf_path): pass

def pptx_a_jpg(pptx_path, jpg_path):
    if comtypes_available:
        if not _convertir_soffice(pptx_path, "jpg", jpg_path): pass
    else:
        if not _convertir_soffice(pptx_path, "jpg", jpg_path): pass

