def upload_attachment_airtable_via_url(token, base_id, table_name, record_id, column_name, drive_url):
    """
    Actualiza el registro de Airtable con la URL p煤blica del archivo de la nube (Cloudinary).
    """
    data = {
        "fields": {
            column_name: [
                {
                    "url": drive_url 
                }
            ]
        }
    }

    url = f"https://api.airtable.com/v0/{base_id}/{table_name}/{record_id}"
    headers = {
        "Authorization": f"Bearer {token}",
        "Content-Type": "application/json"
    }

    try:
        r = requests.patch(url, headers=headers, json=data, timeout=60)
        r.raise_for_status() 
        return True

    except requests.exceptions.RequestException as e:
        print(f"ERROR al subir adjunto (v铆a URL) a Airtable para {record_id}: {r.status_code} - {r.text}")
        messagebox.showerror("Error Airtable", f"Fallo al subir {column_name} (v铆a URL) para {record_id}: {r.status_code}")
        print(f"Respuesta completa de Airtable: {r.text}") 
        return False

