#  FUNCIN DE SANEAMIENTO PARA PUBLIC_ID DE CLOUDINARY
def sanitize_cloudinary_id(name):
    """Limpia el nombre del archivo para usarlo como public_id/folder en Cloudinary."""
    # Eliminar acentos y caracteres no ASCII
    s = unicodedata.normalize('NFKD', name).encode('ascii', 'ignore').decode('ascii')
    # Reemplazar espacios y otros caracteres no seguros por guiones bajos
    #  CRTICO: Mantenemos la barra '/' fuera del saneamiento si se usa como separador.
    s = re.sub(r'[^a-zA-Z0-9_.-/]', '_', s).lower() 
    # Limpiar guiones bajos duplicados
    s = re.sub(r'_{2,}', '_', s)
    return s.strip('_')


def upload_file_to_cloudinary(file_path, file_name, folder_path):
    """
    Sube un archivo a Cloudinary y devuelve la URL de visualizaci贸n, respetando la estructura de carpetas.
    """
    try:
        # Cloudinary necesita un 'public_id' 煤nico sin extensi贸n, lo saneamos
        public_id_base = os.path.splitext(file_name)[0]
        
        # 1. Sanear solo el nombre del archivo (el public_id). 
        # Reemplazamos todos los separadores de ruta por guion bajo para que Cloudinary no cree subcarpetas aqu铆.
        sanitized_public_id = sanitize_cloudinary_id(public_id_base).replace('/', '_').replace('\\', '_') 
        
        # 2. Sanear la ruta de la carpeta (folder_path). 
        # Aqu铆 permitimos la barra '/' para que Cloudinary cree la jerarqu铆a.
        sanitized_folder_path = folder_path.strip('/') # Quitamos cualquier barra inicial/final extra

        # Sube el archivo
        upload_result = cloudinary.uploader.upload(
            file_path,
            public_id=sanitized_public_id,
            folder=sanitized_folder_path, # Usa la ruta para la jerarqu铆a
            overwrite=True, 
            resource_type="auto"
        )
        
        return upload_result.get('secure_url')

    except Exception as e:
        print(f"Error al subir el archivo a Cloudinary: {e}")
        return None
