#  MODIFICADA para aceptar 'subir_nube'
def generar_pptx_output(records_seleccionados, carpeta_destino_base, uso, plantilla_tipo_map, cfg, subir_nube=1):
    
    token = cfg.get("token")
    base_id = cfg.get("base_id")
    table_name = cfg.get("table_name")
    
    if not (token and base_id and table_name):
        messagebox.showerror("Error de Configuraci贸n", "Faltan credenciales de Airtable. Proceso cancelado.")
        return 

    #  1. AUTENTICAR CLOUDINARY ANTES DEL BUCLE (SOLO SI SE SUBE)
    if subir_nube: 
        progress_step(msg="Verificando configuraci贸n de Cloudinary...", inc=0)
        try:
            is_authenticated = get_cloudinary_config() 
            if not is_authenticated: 
                 messagebox.showerror("Error de Configuraci贸n", "Las credenciales de Cloudinary no est谩n configuradas.")
                 return 
        except Exception as e:
            messagebox.showerror("Error de Configuraci贸n", f"Fallo la configuraci贸n de Cloudinary. Error: {e}")
            return 

    total_steps = 0
    total_archivos = len(records_seleccionados)
    
    PUNTOS_POSTAL = 1 * 4        
    PUNTOS_REPORTE_PLANTILLA = 1 * 4  
    PUNTOS_REPORTE_RELLENO = 1 * 4   
    PUNTOS_REPORTE_IMAGEN = 2 * 4    
    PUNTOS_REPORTE_CONVERT = 6 * 4   
    PUNTOS_REPORTE_UPLOAD = 8 * 4    
    
    if uso == "Reportes":
        for rec_id, fields in records_seleccionados:
            total_steps += PUNTOS_REPORTE_PLANTILLA
            total_steps += PUNTOS_REPORTE_RELLENO
            total_steps += PUNTOS_REPORTE_CONVERT
            if subir_nube: total_steps += PUNTOS_REPORTE_UPLOAD 
            for campo, valor in fields.items():
                if isinstance(valor,list) and valor and isinstance(valor[0],dict) and "id" in valor[0]:
                    total_steps += PUNTOS_REPORTE_IMAGEN
    else:
        total_steps = total_archivos * (PUNTOS_POSTAL + (PUNTOS_REPORTE_UPLOAD if subir_nube else 0))
    
    if total_steps == 0: total_steps = 1
    
    progress_reset(total_steps)
    os.makedirs(TEMP_IMG, exist_ok=True)

    for idx, (record_id, record_fields) in enumerate(records_seleccionados, start=1):
        try:
            tipo = record_fields.get("Tipo","") or ""
            sucursal = record_fields.get("Sucursal","") or ""

            # --- Paso 1: Plantilla ---
            msg_actual = f"[{idx}/{total_archivos}] Obteniendo plantilla para {tipo}..."
            inc_plantilla = PUNTOS_REPORTE_PLANTILLA if uso == "Reportes" else 0
            progress_step(msg=msg_actual, inc=inc_plantilla)
            plantilla_path = _obtener_plantilla_para(plantilla_tipo_map, cfg, tipo, uso)
            if not plantilla_path: continue

            # --- Paso 2: Relleno ---
            msg_actual = f"[{idx}/{total_archivos}] Rellenando datos..."
            inc_relleno = PUNTOS_REPORTE_RELLENO if uso == "Reportes" else 0
            progress_step(msg=msg_actual, inc=inc_relleno)
        
            puntos_img = PUNTOS_REPORTE_IMAGEN if uso == "Reportes" else 0
            prs = rellenar_pptx(record_fields, plantilla_path, record_id, uso, puntos_por_imagen=puntos_img)

            # ... (L贸gica de nombres de archivo y rutas) ...
            dt_fecha = _parse_fecha_any(record_fields.get("Fecha", "")) or datetime.datetime.now()
            year = dt_fecha.year
            month_num = f"{dt_fecha.month:02d}"
            month_name = _MESES[dt_fecha.month - 1].capitalize()
            ruta = record_fields.get("Ruta a seguir", "")
            punto = record_fields.get("Punto de reunion", "")
            municipio = record_fields.get("Municipio", "")
            
            nombre_archivo = f"{formatear_fecha(record_fields.get('Fecha', ''), uso, solo_nombre=True)} - {tipo} {sucursal}"
            
            elegido = ruta if len(ruta) <= len(punto) else punto if ruta or punto else ""
            partes_finales = [p for p in [elegido, municipio] if p]
            if partes_finales:
                nombre_archivo += " - " + ", ".join(partes_finales)
                
            nombre_archivo = proper_refinado(nombre_archivo)
            nombre_archivo_fs = safe_filename(nombre_archivo)
            sucursal_fs = safe_filename(proper_refinado(sucursal))
            ruta_final = os.path.join(carpeta_destino_base, f"{year}/{uso}/{month_num} - {month_name}", sucursal_fs)
            os.makedirs(ruta_final, exist_ok=True)
            nombre_pptx_final = nombre_archivo_fs + ".pptx"
            ruta_pptx = os.path.join(ruta_final, nombre_pptx_final)
            output_extension = ".pdf" if uso == "Reportes" else ".jpg"
            ruta_final_deseada = os.path.join(ruta_final, nombre_archivo_fs + output_extension)
        
            prs.save(ruta_pptx)
        
            # --- Paso 3: Conversi贸n ---
            msg_actual = f"[{idx}/{total_archivos}] Convirtiendo a {output_extension.upper()[1:]}"
            inc_conv_antes = 4 if uso == "Reportes" else 0
            progress_step(msg=msg_actual, inc=inc_conv_antes)
        
            if uso == "Reportes": pptx_a_pdf(ruta_pptx, ruta_final_deseada)
            else: pptx_a_jpg(ruta_pptx, ruta_final_deseada)
        
            inc_conv_despues = (PUNTOS_REPORTE_CONVERT - inc_conv_antes) if uso == "Reportes" else 0
            progress_step(inc=inc_conv_despues)
            # ----------------------------------------------------
        
            #  PASO 4: OBTENER URL DE CLOUDINARY Y SUBIR A AIRTABLE (CONDICIONAL)
            
            if subir_nube:
                nombre_archivo_drive = nombre_archivo_fs + output_extension

                # Definir la ruta de la carpeta en Cloudinary 
                month_name_no_space = month_name.replace(' ', '')
                # Usamos '/' para la estructura de carpetas
                cloud_folder_path = f"Provident/{year}/{uso}/{month_num}_{month_name_no_space}/{sucursal_fs}" 

                progress_step(msg=f"[{idx}/{total_archivos}] Subiendo {nombre_archivo_drive} a Cloudinary...", inc=PUNTOS_REPORTE_UPLOAD // 2)
                
                # Llama a la funci贸n de subida a Cloudinary
                drive_url = upload_file_to_cloudinary(ruta_final_deseada, nombre_archivo_drive, cloud_folder_path)

                
                if drive_url:
                    progress_step(msg=f"[{idx}/{total_archivos}] Subiendo URL a Airtable...", inc=PUNTOS_REPORTE_UPLOAD // 4)
                    columna_airtable = "Postal" if uso == "Postales" else "Reporte"
                    
                    upload_attachment_airtable_via_url(
                        token, base_id, table_name, record_id, columna_airtable, drive_url
                    )
                else:
                    print(f"[ERROR] No se pudo subir el archivo {nombre_archivo_drive} a Cloudinary. Verifique logs.")
                    messagebox.showwarning("Advertencia Cloudinary", f"No se pudo subir el archivo: {nombre_archivo_drive}. Revise la consola para detalles.")

                # Ajuste de progreso final para UPLOAD
                progress_step(inc=PUNTOS_REPORTE_UPLOAD - (PUNTOS_REPORTE_UPLOAD // 4) * 3)
            
            else: # Si NO se sube a la nube
                progress_step(msg=f"[{idx}/{total_archivos}] Subida a la nube omitida (Solo guardado local).", inc=PUNTOS_REPORTE_UPLOAD)

            # --- Incremento Final para Postales (si aplica) ---
            inc_postal_final = PUNTOS_POSTAL if uso == "Postales" else 0
            if inc_postal_final > 0: progress_step(inc=inc_postal_final)
        
        except Exception as e:
            messagebox.showerror("Error en Generaci贸n", f"Ocurri贸 un error inesperado al procesar un registro:\n{e}")
            continue

    if os.path.exists(TEMP_IMG):
        for f in os.listdir(TEMP_IMG):
            try: os.remove(os.path.join(TEMP_IMG, f))
            except: pass
        try: os.rmdir(TEMP_IMG) 
        except: pass

    _progress_ctx["val"] = total_steps
    progress_step(msg="Proceso finalizado. Registros procesados: " + str(len(records_seleccionados)), inc=0)
    
    if not comtypes_available:
        messagebox.showinfo("Proceso de Conversi贸n",
                             f"Proceso finalizado. Verifique las carpetas de salida. "
                             f"Si la conversi贸n fall贸, el **.pptx** con el nombre correcto "
                             f"se mantuvo para exportaci贸n manual.")

# ----------------- Funciones de L贸gica de la Interfaz Unificada -----------------
def cargar_bases(entry_token, combo_base, combo_table, root):
    tkn = entry_token.get().strip()
    if not tkn:
        messagebox.showerror("Error", "Ingresa un token", parent=root)
        return
    try:
        headers = {"Authorization": f"Bearer {tkn}"}
        resp = requests.get("https://api.airtable.com/v0/meta/bases", headers=headers)
        resp.raise_for_status()
        
        data = resp.json()
        bases = data.get("bases", [])
        base_list = [f"{b['name']} ({b['id']})" for b in bases]
        
        # Siempre limpiar la tabla, ya sea que haya bases o no
        combo_table.set("")
        combo_table["values"] = []
        combo_base["values"] = base_list

        if base_list:
            cfg = _cargar_config()
            base_id_saved = cfg.get("base_id", "")
        
            found = False
            for i, base_item in enumerate(base_list):
                if base_id_saved in base_item:
                    combo_base.current(i)
                    found = True
                    break
        
            # Si no se encuentra la base guardada, seleccionar el primer elemento (铆ndice 0)
            if not found:
                combo_base.current(0)
        
            # Intentar cargar tablas solo si se seleccion贸 una base
            if combo_base.get():
                cargar_tablas(entry_token, combo_base, combo_table, root, initial_load=True)
        else:
            # Si la lista de bases est谩 vac铆a, mostrar mensaje
            combo_base.set("")
            messagebox.showinfo("Info", "No se encontraron bases con este Token. Verifica que el Token tenga los permisos de Lectura/Metadatos necesarios.", parent=root)
    except Exception as e:
        # 锔 Si el error persiste, el problema es en la API o el Token
        messagebox.showerror("Error", f"No se pudieron cargar bases. Verifica el token: {e}", parent=root)

def cargar_tablas(entry_token, combo_base, combo_table, root, initial_load=False):
    tkn = entry_token.get().strip()
    sel = combo_base.get()
    if "(" not in sel:
        if not initial_load: messagebox.showwarning("Advertencia", "Selecciona una base v谩lida de la lista.", parent=root)
        return
    b_id = sel.split("(")[-1].replace(")", "")
    try:
        headers = {"Authorization": f"Bearer {tkn}"}
        resp = requests.get(f"https://api.airtable.com/v0/meta/bases/{b_id}/tables", headers=headers)
        resp.raise_for_status()
        tables = resp.json().get("tables", [])
        table_list = [t["name"] for t in tables]
        combo_table["values"] = table_list
        if table_list:
            cfg = _cargar_config()
            table_name_saved = cfg.get("table_name", "")
            found = False
            for i, table_item in enumerate(table_list):
                if table_name_saved == table_item:
                    combo_table.current(i)
                    found = True
                    break
            if not found and table_list:
                combo_table.current(0)
        else:
            combo_table.set("")
            if not initial_load: messagebox.showinfo("Info", "No se encontraron tablas en esta Base.", parent=root)
    except Exception as e:
        messagebox.showerror("Error", f"No se pudieron cargar tablas: {e}", parent=root)

def _key_sort(rec):
    fields = rec.get("fields", {})
    suc = str(fields.get("Sucursal","")).lower()
    tip = str(fields.get("Tipo","")).lower()
    return (suc, tip)

def cargar_datos_y_treeview(root, entry_token, combo_base, combo_table, tree, record_map):
    token = entry_token.get().strip()
    sel_base = combo_base.get()
    sel_table = combo_table.get()

    if not token or "(" not in sel_base or not sel_table:
        messagebox.showwarning("Advertencia", "Aseg煤rate de haber seleccionado Token, Base y Tabla.", parent=root)
        return
    
    b_id = sel_base.split("(")[-1].replace(")", "")
    cfg = _cargar_config()
    cfg["token"] = token
    cfg["base_id"] = b_id
    cfg["table_name"] = sel_table
    _guardar_config(cfg)
    
    progress_step(msg="Cargando registros desde Airtable...", inc=0)
    root.update()

    records_raw = get_records(token, b_id, sel_table)

    if not records_raw:
        messagebox.showinfo("Info", "No hay registros en la tabla seleccionada.")
        progress_step(msg="Listo.", inc=0)
        return
    
    records_raw.sort(key=_key_sort)
    
    for i in tree.get_children(): tree.delete(i)
    record_map.clear()

    cols = tree["columns"]

    for rec in records_raw:
        fields = rec.get("fields", {})
        values = [limpiar_texto(fields.get(col, "")) for col in cols]
        tree.insert("", "end", iid=rec["id"], values=values)
        record_map[rec["id"]] = fields

    autoajustar_columnas(tree)
    progress_step(msg=f"Carga completa. **{len(records_raw)}** registros listos.", inc=0)

def autoajustar_columnas(tv, min_w=80, max_w=320, pad=24):
    fnt = tkfont.Font(family="Segoe UI", size=10)
    for col in tv["columns"]:
        width_px = fnt.measure(col)
        for iid in tv.get_children(""):
            val = tv.set(iid, col)
            width_px = max(width_px, fnt.measure(str(val)))
        width_px = max(min_w, min(width_px + pad, max_w))
        tv.column(col, width=width_px, anchor="center")

